import React, { useState } from 'react'
import './notepad.css'


const Notepad = () => {

    const [inputlist, setinputlist] = useState("");
    const [notes, setnotes] = useState([]);

    const addNote = (event) => {
        setinputlist(event.target.value);
    };

    const listNote = () => {
        setnotes((oldenotes) => {
            return [...oldenotes, inputlist];
        });
        setinputlist(' ');
    }
        return(
            <div className="notepadbox">
                <p className="heading">Notepad</p>
                <br />

                <ol>
                    <div className= "noteslist">Title<li> Lorem ipsum dolor sit am...</li></div>
                    <div className= "noteslist">Title<li> Lorem ipsum dolor sit am...</li></div>
                    <div className= "noteslist">Title<li> Lorem ipsum dolor sit am...</li></div>
                    <div className= "noteslist">Title<li> Lorem ipsum dolor sit am...</li></div>
                    <div className= "noteslist">Title<li> Lorem ipsum dolor sit am...</li></div>
                    <div className= "noteslist">Title<li> Lorem ipsum dolor sit am...</li></div>

                    {notes.map( (notesval) => {
                        return <div className= "noteslist">Title<li>{notesval}</li></div>
                    })}
                </ol>
                <label> Title </label>
                <input className="inputbox" type="text" placeholder="add your next work list" value={inputlist} onChange={addNote} />
                <button className="addbtn" onClick={listNote}> + </button>


            </div>
        )

}
export default Notepad;
