import React, { useState } from 'react'
import './posts.css'
import PostIcon from './image/post.png'

const Postpage = () => {

    const [inputlist, setinputlist] = useState("");
    const [posts, setposts] = useState([]);

    const addPost = (event) => {
        setinputlist(event.target.value);
    };

    const listPost = () => {
        setposts((oldposts) => {
            return [...oldposts, inputlist];
        });
        setinputlist(' ');
    }
        return(
            <div className="postbox">
                {/* <br /> */}
                <div className="inputdivbox">
                {/* <label> Title </label> */}
                <input className="inputpostbox" type="text" placeholder="Share something with your class.." value={inputlist} onChange={addPost} />
                <button className="postbtn" onClick={listPost}> <img src={PostIcon} /> </button>
                </div>
                <div>
                    <div className= "postlist">Title &nbsp;<li> Lorem ipsum dolor sit am...</li></div>


                    {posts.map( (poststext) => {
                        return <div className= "postlist">Title &nbsp;<li>{poststext}</li> <br /></div>
                    })}
                </div>

            </div>
        )

}
export default Postpage;
