import React, { useState } from 'react'
import './posts.css'


const Postpage = () => {

    const [inputlist, setinputlist] = useState("");
    const [posts, setposts] = useState([]);

    const addPost = (event) => {
        setinputlist(event.target.value);
    };

    const listPost = () => {
        setposts((oldposts) => {
            return [...oldposts, inputlist];
        });
        setinputlist(' ');
    }
        return(
            <div className="notepadbox">
                {/* <p className="heading">Notepad</p> */}
                <br />

                <div>
                    <div className= "noteslist">Title &nbsp;<li> Lorem ipsum dolor sit am...</li></div>
                    <div className= "noteslist">Title &nbsp;<li> Lorem ipsum dolor sit am...</li></div>
                    <div className= "noteslist">Title &nbsp;<li> Lorem ipsum dolor sit am...</li></div>
                    <div className= "noteslist">Title &nbsp;<li> Lorem ipsum dolor sit am...</li></div>
                    <div className= "noteslist">Title &nbsp;<li> Lorem ipsum dolor sit am...</li></div>


                    {posts.map( (poststext) => {
                        return <div className= "noteslist">Title &nbsp;<li>{poststext}</li> <br /></div>
                    })}
                </div>
                <div className="inputdivbox">
                <label> Title </label>
                <input className="inputbox" type="text" placeholder="add your next work list" value={inputlist} onChange={addPost} />
                <button className="addbtn" onClick={listPost}> + </button>
                </div>


            </div>
        )

}
export default Postpage;
