import React, { useState } from 'react'
import './notepad.css'


const Notepad = () => {

    const [inputlist, setinputlist] = useState("");
    const [notes, setnotes] = useState([]);
    const addNote = (e) => {
        setinputlist(e.target.value);
    };

    const listNote = () => {
        setnotes((oldenotes) => {
            return [...oldenotes, inputlist];
        })
    }
        return(
            <div className="notepadbox">
                <p className="heading">Notepad</p>
                <br />

                <ol>
                    {notes.map( (notesval) => {
                        return <li>{inputlist}</li>
                    })}
                </ol>

                <input type="text" placeholder="add your next work list" onChange={addNote} />
                <button className="addbtn" onClick={listNote}> + </button>
            </div>
        )

}
export default Notepad;
