import React, { useState } from 'react'
import './posts.css'
import PostIcon from './image/post.png'
import AttachIcon from './image/Vector.png'
import Senddrop from './image/Vector 2.png'
const Postpage = () => {

    const [inputlist, setinputlist] = useState("");
    const [posts, setposts] = useState([]);

    const addPost = (event) => {
        setinputlist(event.target.value);
    };

    const listPost = () => {
        setposts((oldposts) => {
            return [...oldposts, inputlist];
        });
        setinputlist(' ');
    }
        return(
            <div className="postbox">
                {/* <br /> */}
                <div className="inputdivbox">
                {/* <label> Title </label> */}
                <div className="innerinputbox">
                    <input className="inputpostbox" type="text" placeholder="Share something with your class.." value={inputlist} onChange={addPost} />

               </div>
               <div className="allbtndiv">
                     <button className="cancelbtn">Cancel</button>
                    <button className="postbtn" > <img src={AttachIcon} /> </button>
                    <button className="postbtn" onClick={listPost}> Share <img src={Senddrop} /> </button>
               {/* <button className="postbtn" onClick={listPost}> <img src={PostIcon} /> </button> */}

               </div>
                </div>
                <div>
                    <div className= "postlist"> &nbsp;<li> Lorem ipsum dolor sit am...</li></div>


                    {posts.map( (poststext) => {
                        return <div className= "postlist">Title &nbsp;<li>{poststext}</li> <br /></div>
                    })}
                </div>

            </div>
        )

}
export default Postpage;
